<?php if ( ! empty($stations)): ?><br><?php foreach ($stations as $s): ?><div class="row event-station">		<div class="two columns alpha op-callsign">		<span class="callsign"><?php echo $s->operator->o_callsign() ?></span>	</div>		<div class="six columns op-name">		<?php echo anchor('operators/' . $s->operator->o_slug(), $s->operator->o_name()) ?>		<br><span class="op-type"><?php echo $s->operator->o_type('longtext') ?></span>	</div>		<div class="two columns r-photo">		<?php if (count($s->railway->images) > 0): ?>		<img src="<?php echo $s->railway->images[0]->src('c150x150') ?>" width="50" height="50">		<?php else: ?>		<img src="" width="50" height="50">		<?php endif; ?>	</div>		<div class="six columns omega r-name">		<?php echo anchor('railways/' . $s->railway->r_slug(), $s->railway->r_name()) ?>	</div>	</div><?php endforeach; ?><?php else: ?><p>No stations have been registered!</p><?php endif; ?>