<?php if ($news): ?><?php foreach ($news as $n): ?><!-- 12 cols to play with --><div class="row article">		<div class="three columns alpha article-meta">		<p class="article-datetime"><?php echo $n->n_datetime_posted('d M Y H:i') ?></p>				<?php if ($n->n_e_year()): ?>		<p class="article-event">			About the <a href="<?php echo site_url('stations/' . $n->n_e_year()) ?>" title="View stations registered for this event"><?php echo $n->n_e_year() ?></a> event		</p>		<?php endif; ?>				<?php if ($n->n_o_id()): ?>		<p class="article-author">			Posted by <span class="article-author-callsign"><?php echo $n->operator->o_callsign() ?></span> (<span class="article-author-name"><?php echo $n->operator->o_name() ?></span>)		</p>		<?php endif; ?>				<?php if ($n->n_r_id()): ?>		<p class="article-railway">			Related to <a href="<?php echo $n->railway->public_url() ?>" title="View railway information"><?php echo $n->railway->r_name() ?></a>		</p>		<?php endif; ?>	</div>		<div class="nine columns omega article-content">		<h3><?php echo $n->n_title() ?></h3>		<?php echo word_limiter($n->n_content(), 75) ?>		<?php echo $n->read_more_link() ?>	</div>		<div class="clear"></div>	</div><?php endforeach; ?><?php else: ?><div class="alert info">Sorry! Slow news day &mdash; nothing for you to read.</div><?php endif; ?>