<?php if ($news): ?><?php foreach ($news as $n): ?><div class="row article">		<div class="three columns alpha article-meta">		<p class="article-datetime"><?php echo $n->n_datetime_posted('d M Y H:i') ?></p>				<?php if ($n->n_e_year()): ?>		<p class="article-event">			About the <a href="<?php echo site_url('news/' . $n->n_e_year()) ?>" title="View stations registered for this event"><?php echo $n->n_e_year() ?></a> event		</p>		<?php endif; ?>				<?php if ($n->n_o_id()): ?>		<p class="article-author">			Posted by <span class="article-author-callsign"><?php echo anchor('news/author/' . $n->operator->o_slug(), $n->operator->o_callsign()) ?></span> (<span class="article-author-name"><?php echo $n->operator->o_name() ?></span>)		</p>		<?php endif; ?>				<?php if ($n->n_r_id()): ?>		<p class="article-railway">			Related to <?php echo anchor('news/railway/' . $n->railway->r_slug(), $n->railway->r_name()) ?>		</p>		<?php endif; ?>	</div>		<div class="nine columns article-content">		<h2><?php echo $n->n_title() ?></h2>		<?php echo word_limiter(strip_tags($n->n_content_html()), 70) ?>		<?php echo $n->read_more_link() ?>	</div>		<div class="four columns omega article-image">		<?php if ($n->images): ?>		<img src="<?php echo $n->images[0]->src('c220x145') ?>" alt="Image for <?php echo $n->n_title() ?>">		<?php endif; ?>	</div>		<div class="clear"></div>	</div><?php endforeach; ?><?php else: ?><div class="alert info">Sorry! Slow news day &mdash; nothing for you to read.</div><?php endif; ?>