<style>ul.qq-upload-list { margin: 10px 0 0 15px; }</style><?php if ($this->session->flashdata('new_railway_id')): ?><div class="alert success">	You added a new railway!	<?php echo anchor('railways/edit/' . $this->session->flashdata('new_railway_id'),		'Click here to add more details about ' . $this->session->flashdata('new_railway_name') . '.'); ?></div><?php endif; ?><?php if ($stations): ?><?php $last_year = NULL; ?><?php foreach ($stations as $station): ?><?php $year_diff = ($last_year !== $station->s_e_year()); ?><!-- 12 cols to play with --><div class="row station <?php if ($year_diff) echo 'heading'; ?>">		<div class="one column alpha station-year">		<?php		if ($last_year !== $station->s_e_year())		{			echo '<h3>' . $station->s_e_year() . '</h3>';			$last_year = $station->s_e_year();		}		else		{			echo '&nbsp;';		}		?>	</div>		<div class="seven columns station-operator station-railway">		<span class="callsign"><?php echo $station->s_callsign() ?></span>		<span class="name"><?php echo $station->operator->o_name() ?></span>		<span class="railway"><?php echo $station->railway->r_name(); ?></span>		<br>		<?php		if ($station->s_e_year() == $current_event->e_year())		{			echo $station->shack_edit_button();			echo $station->shack_delete_button();		}		?>	</div>		<div class="four columns omega log-status">				<?php if ($station->s_log_status() !== 'ignore'): ?>					<?php if ($station->s_e_year() < $current_event->e_year() && $station->s_log_status() == 'waiting'): ?>						<div id="file-uploader-<?php echo $station->s_id() ?>">						<noscript>					<input type="file" name="userfile" size="20">				</noscript>         			</div>						<script>			jsq.add(function() {				var uploader = new qq.FileUploader({					element: document.getElementById('file-uploader-<?php echo $station->s_id() ?>'),					multiple: false,					action: siteurl + 'shack/stations/upload_log',					debug: false,					buttonText: "Upload Log",					params: {						s_id: <?php echo $station->s_id() ?>					},					onComplete: function(id, file_name, res) {						if (res.status == 'err') {							var ul = $(this.element).find("ul");							alert("Error: " + res.msg);						} else {							location.reload(true);						}					}				});			});			</script>						<?php endif; ?>				<?php endif; ?>				<?php if ($station->s_log_status() == 'uploaded'): ?>		<span class="orange label">Awaiting log verification</span>		<?php endif; ?>				<?php if ($station->s_log_status() == 'verified'): ?>		<span class="green label"><?php echo $station->s_num_contacts() ?> contacts</span>		<p style="margin-top: 8px">			<small><a href="#" class="i cert">Download certificate</a></small>		</p>		<?php endif; ?>			</div>		<div class="clear"></div>	</div><?php endforeach; ?><?php else: ?><div class="alert info">You haven&rsquo;t registered any stations. <?php echo anchor('shack/stations/register', 'Register one now!') ?></div><?php endif; ?>